package vn.edu.iuh.fit.controller;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.web.bind.annotation.GetMapping;import org.springframework.web.bind.annotation.PathVariable;import org.springframework.web.bind.annotation.RestController;import vn.edu.iuh.fit.entity.User;import vn.edu.iuh.fit.repositories.UserRepository;import java.util.List;@RestControllerpublic class UserController {    @Autowired    private UserRepository userRepository;    @GetMapping("/api/user/{id}")    public User getUser(@PathVariable("id") long id){        User user=userRepository.findById(id).get();        return user;    }    @GetMapping("/api/user")    public List<User> getAllUser(){        List<User> list = userRepository.findAll();        return list;    }}