package vn.edu.iuh.fit.controller;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.web.bind.annotation.GetMapping;import org.springframework.web.bind.annotation.PathVariable;import org.springframework.web.bind.annotation.RestController;import vn.edu.iuh.fit.entity.Order;import vn.edu.iuh.fit.repositories.OrderRepository;import java.util.List;@RestControllerpublic class OrderController {    @Autowired    private OrderRepository orderRepository;    @GetMapping("/api/orders/list")    public List<Order> getAll(){        List<Order> list = orderRepository.findAll();        return list;    }    @GetMapping("/api/orders/{id}")    public Order getOrder(@PathVariable("id")long id){        Order order = orderRepository.findById(id).get();        return order;    }}