package vn.edu.iuh.fit.entity;import jakarta.persistence.*;import lombok.*;@Entity@Table(name="orders")@Data@NoArgsConstructor@AllArgsConstructor@Getter @Setterpublic class Order {    @Id    @GeneratedValue(strategy = GenerationType.IDENTITY)    @Column(name = "order_id")    private long id;    @Column(name="total_price")    private double totalPrice;    public Order(double totalPrice) {        this.totalPrice = totalPrice;    }    @Override    public String toString() {        return "Order{" +                "id=" + id +                ", totalPrice=" + totalPrice +                '}';    }}