package vn.edu.iuh.fit.controller;import org.springframework.web.bind.annotation.GetMapping;import org.springframework.web.bind.annotation.PathVariable;import org.springframework.web.bind.annotation.RestController;import org.springframework.web.client.RestTemplate;import vn.edu.iuh.fit.entity.Order;import java.util.Arrays;import java.util.List;@RestControllerpublic class ApiGetwayController {    private RestTemplate restTemplate=new RestTemplate();        @GetMapping("/apigetway/{service}")        public List<Order> getService(@PathVariable("service") String service) {            if (service.equals("orders")) {                Order[] orders = restTemplate.getForObject("http://localhost:8083/api/orders/list", Order[].class);                return Arrays.asList(orders);            } else {                return null;            }        }    }