package vn.edu.iuh.fit.entity;import jakarta.persistence.*;import lombok.*;@Entity@Table(name="products")@Getter @Setter@AllArgsConstructor@NoArgsConstructor@Datapublic class Product {    @Id    @GeneratedValue(strategy = GenerationType.IDENTITY)    @Column(name="product_id")    private long productId;    @Column(name="product_name")    private String productName;    @Column(name="price")    private double price;    @Column(name="user_id")    private long userId;    @Column(name="user_name")    private String userName;    public Product(String productName, double price, long userId, String userName) {        this.productName = productName;        this.price = price;        this.userId = userId;        this.userName = userName;    }    @Override    public String toString() {        return "Product{" +                "productId=" + productId +                ", productName='" + productName + '\'' +                ", price=" + price +                ", userId=" + userId +                ", userName='" + userName + '\'' +                '}';    }}